import {Address, Cell, toNano} from '@ton/core';
import { compile, NetworkProvider } from '@ton/blueprint';
import {PtjRouter, ptjRouterConfigToCell} from "../wrappers/PtjRouter";

export async function run(provider: NetworkProvider) {
    const sender = provider.sender().address!;
    const minter = Address.parse("kQAGs82fyuPTvolTdKHWfutaoi7NGN71C4TvoHJ0D48Pwrip");
    const router = provider.open(PtjRouter.createFromAddress(Address.parse('kQB7so4wY0oydkyg1xnUHufbWT2TVZwmi4V6pQSxkCNPy-cD')));

    const newCode =  await compile('PtjRouter');

    await router.sendCodeUpgrade(provider.sender(), {
        newCode,
        newData: ptjRouterConfigToCell(
            {
                isActive: true,
                owner: sender,
                secondOwner: sender,
                jettonMasterAddress: minter,
                jettonWalletCode: Cell.fromBase64(""),
                factors: {
                    burn: {
                        factor: 5n,
                        base: 10n,
                    },
                    fee: {
                        factor: 1n,
                        base: 10n,
                    },
                },
                minBurn: toNano('1000')
            }
        )
    });
}
